import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FlightService } from './../api/services/flight.service';
import { FlightRm } from '../api/models/flight-rm';
import { AuthService } from '.././auth/auth.service';
import {FormBuilder } from '@angular/forms'
import { BookDto } from '../api/models';
@Component({
  selector: 'app-book-flights',
  templateUrl: './book-flights.component.html',
  styleUrls: ['./book-flights.component.css']
})
export class BookFlightsComponent implements OnInit{
  
  constructor(private route: ActivatedRoute,
    private router: Router,
    private flightService: FlightService,
    private authService: AuthService,
    private fb: FormBuilder) { }

  flightId: string = 'not loaded'
  flight: FlightRm = {}

  form = this.fb.group({
    number:[1]
  })

  ngOnInit(): void {
    if (!this.authService.currentUser)
      this.router.navigate(['/register-passenger'])

    this.route.paramMap.subscribe(p => this.findFlight(p.get("flightId")))
  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? 'not passed';

    this.flightService.findFlight({ id: this.flightId })
      .subscribe(flight => this.flight = flight, this.handleError)
  }

  private handleError = (err: any) => {
    if (err.status == 404 || err.status == 400) {
      alert("Рейс не найден")
        this.router.navigate(['/search-flight'])
    }
    console.log("Статус ошибки:", err.status)
    console.log("Текст ошибки:", err.statusText)
    console.log(err)
  }

  book() {
    console.log(`Забронировано ${this.form.get('number')?.value} мест на рейс: ${this.flight.id}`);

    const numberValue = this.form.get('number')?.value;
    const numberOfSeats: number | undefined = (numberValue !== null && numberValue !== undefined) ? numberValue : undefined;

    const booking:BookDto = {
      flightId: this.flight.id,
      passengerEmail: this.authService.currentUser?.email,
      numberOfSeats: numberOfSeats
    }

    this.flightService.bookFlight({ body: booking }).
      subscribe(_ => console.log('Регистрация прошла успешно'), e => console.error)
  }
}

<h5 class="text-md-center m-5">Забронировать рейс</h5>

<div class="card-body text-center" style="padding: 20px; background-color:#e5ecf26e">
  <h6 class="card-title ">{{flight.airline}}</h6>
  <div class="card-title" style="color: green">Мест осталось {{flight.remainingNumberOfSeats}}</div>
  <div class="d-grid text-center g-2">
    <div class="row">
      <div class="col">
        <div class="col m-3">

          <i class="text-primary fas fa-plane-departure" style="font-size: 20px;"></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{flight.departure?.place}}
          </i>

        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{flight.departure?.time | date}}

        </div>
        <div class="col">

          <i class="fa-regular fa-clock">
            {{flight.departure?.time | date:'shortTime'}}
          </i>
        </div>
      </div>

      <div class="col">
        <div style="display: flex;">
          <i class="fa-solid  fa-circle" style="margin-top:51px; font-size: 9px;"></i>
          <hr style="display:block;width: 100%;margin-top: 55px;">
          <i class="fas fa-plane" style="margin-top: 48px;"></i>


        </div>
        <i class="fa fa-rub" aria-hidden="true">{{flight.price}}</i>
        <br>

      </div>
      <div class="col">
        <div class="col m-3">
          <i class="text-primary fas fa-plane-arrival" style="font-size: 20px;"></i>

        </div>
        <div class="col">

          <i class="fa-solid fa-location-dot">
            {{flight.arrival?.place}}
          </i>
        </div>
        <div class="col m-2 text-primary">
          <i class="fa-regular "></i>
          {{flight.arrival?.time | date}}

        </div>
        <div class="col">
          <i class="fa-regular fa-clock">
            {{flight.arrival?.time | date:'shortTime'}}
          </i>

        </div>
      </div>
    </div>
  </div>
</div>

<div class="card-footer d-flex justify-content-center" style="padding: 5px; background-color: #E5E4E2 ">

  <form [formGroup]="form" (ngSubmit)="book()">
    <label class="form-label mt-5" for="numberOfSeats">Количество мест</label>
    <div class="center input-group">

      <input type="number" formControlName="number" class="form-control" id="numberOfSeats" name="numberOfSeats"
             placeholder="Количество мест" />
      <button type="submit" class="btn btn-primary">
        <i class="fas fa-ticket"></i>
        Забронировать
      </button>
    </div>
  </form>

</div>
